    "compilerOptions": {
        "noUncheckedIndexedAccess": true
    }
}
`````

`````ts
type T = {
  [key: string]: number | string;
}


function f(p: T) {
  /**
   * –û–±—Ä–∞—â–µ–Ω–∏–µ –∫ –Ω–µ—Å—É—â–µ—Å—Ç–≤—É—é—â–∏–º –ø–æ–ª—è–º
   */
  p.bad.toString(); // Error -> TS2532: Object is possibly 'undefined'.
  p[Math.random()].toString(); // Error -> TS2532: Object is possibly 'undefined'.


  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞–ª–∏—á–∏—è –ø–æ–ª—è bad
  if ("bad" in p) {
      p.bad?.toString(); // Ok
  }

  // –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –æ–ø—Ü–∏–æ–Ω–∞–ª—å–Ω–æ–≥–æ –æ–ø–µ—Ä–∞—Ç–æ—Ä–∞
  p[Math.random()]!.toString(); // Ok -> –æ—à–∏–±–∫–∞ –≤–æ –≤—Ä–µ–º—è –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è

  p[Math.random()]?.toString();  // Ok -> –û—à–∏–±–∫–∞ –Ω–µ –≤–æ–∑–Ω–∏–∫–Ω–µ—Ç
}
`````

–ù–µ –±—É–¥–µ—Ç –ª–∏—à–Ω–∏–º —É–ø–æ–º—è–Ω—É—Ç—å, —á—Ç–æ –≤–ª–∏—è–Ω–∏–µ –¥–∞–Ω–Ω–æ–≥–æ –º–µ—Ö–∞–Ω–∏–∑–º–∞ —Ä–∞—Å–ø—Ä–æ—Å—Ç—Ä–∞–Ω—è–µ—Ç—Å—è —Ç–∞–∫–∂–µ –∏ –Ω–∞ –º–∞—Å—Å–∏–≤—ã. –í —Å–ª—É—á–∞–µ —Å –º–∞—Å—Å–∏–≤–æ–º –Ω–µ –ø–æ–ª—É—á–∏—Ç—Å—è –∏–∑–±–µ–∂–∞—Ç—å –∞–Ω–∞–ª–æ–≥–∏—á–Ω–æ–π –æ—à–∏–±–∫–∏ –ø—Ä–∏ –ø–æ–ø—ã—Ç–∫–µ –æ–±—Ä–∞—â–µ–Ω–∏—è –∫ –µ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞–º –ø—Ä–∏ –ø–æ–º–æ—â–∏ –∏–Ω–¥–µ–∫—Å–Ω–æ–π —Å–∏–≥–Ω–∞—Ç—É—Ä—ã.

