  gender?: string;
}

type Person = Readonly<IPerson> // type Person = { readonly gender?: string; }
`````

–í –∫–∞—á–µ—Å—Ç–≤–µ –ø—Ä–∏–º–µ—Ä–∞ –º–æ–∂–Ω–æ –ø—Ä–∏–≤–µ—Å—Ç–∏ —á–∞—Å—Ç–æ –≤—Å—Ç—Ä–µ—á–∞—é—â–µ–π—Å—è –Ω–∞ –ø—Ä–∞–∫—Ç–∏–∫–µ —Å–ª—É—á–∞–π, –≤ –∫–æ—Ç–æ—Ä–æ–º —É–Ω–∏–≤–µ—Ä—Å–∞–ª—å–Ω—ã–π –∏–Ω—Ç–µ—Ä—Ñ–µ–π—Å –æ–ø–∏—Å—ã–≤–∞–µ—Ç –æ–±—ä–µ–∫—Ç –ø—Ä–µ–¥–Ω–∞–∑–Ω–∞—á–µ–Ω–Ω—ã–π –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å –¥–∞–Ω–Ω—ã–º–∏. –ü–æ—Å–∫–æ–ª—å–∫—É –≤ –ª—å–≤–∏–Ω–æ–π –¥–æ–ª–µ –¥–∞–Ω–Ω—ã–µ –ø—Ä–µ–¥—Å—Ç–∞–≤–ª—è—é—Ç—Å—è –æ–±—ä–µ–∫—Ç–Ω—ã–º–∏ —Ç–∏–ø–∞–º–∏, –∏–Ω—Ç–µ—Ä—Ñ–µ–π—Å –¥–µ–∫–ª–∞—Ä–∏—Ä—É–µ—Ç –∏—Ö –∫–∞–∫ –Ω–µ–∏–∑–º–µ–Ω—è–µ–º—ã–µ, —á—Ç–æ –≤ –¥–∞–ª—å–Ω–µ–π—à–µ–º, –ø—Ä–∏ –µ–≥–æ —Ä–µ–∞–ª–∏–∑–∞—Ü–∏–∏, –∏–∑–±–∞–≤–∏—Ç —Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫–∞ –≤ —Ç–∏–ø–∏–∑–∞—Ü–∏–∏ –∫–æ–Ω—Å—Ç—Ä—É–∫—Ü–∏–π –∏ —Ç–µ–º —Å–∞–º—ã–º —Å—ç–∫–æ–Ω–æ–º–∏–≤ –¥–ª—è –Ω–µ–≥–æ –≤—Ä–µ–º—è –Ω–∞ –±–æ–ª–µ–µ —É–≤–ª–µ–∫–∞—Ç–µ–ª—å–Ω—ã–µ –∑–∞–¥–∞—á–∏.

`````ts
/**
 * –ò–Ω—Ç–µ—Ä—Ñ–µ–π—Å –Ω–µ–æ–±—Ö–æ–¥–∏–º –¥–ª—è –æ–ø–∏—Å–∞–Ω–∏—è —ç–∫–∑–µ–º–ø–ª—è—Ä–∞
 * –ø—Ä–æ–≤–∞–π–¥–µ—Ä–æ–≤ —Å –∫–æ—Ç–æ—Ä—ã–º–∏ –±—É–¥–µ—Ç —Å–æ–ø—Ä—è–∂–µ–Ω–Ω–æ
 * –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–µ. –ö—Ä–æ–º–µ —Ç–æ–≥–æ, –∏–Ω—Ç–µ—Ä—Ñ–µ–π—Å –æ–ø–∏—Å—ã–≤–∞–µ—Ç
 * –ø–æ—Å—Ç–∞–≤–ª—è–µ–º—ã–µ –¥–∞–Ω–Ω—ã–µ –∫–∞–∫ —Ç–æ–ª—å–∫–æ –¥–ª—è —á—Ç–µ–Ω–∏—è,
 *, —á—Ç–æ –≤ –±—É–¥—É—â–µ–º –º–æ–∂–µ—Ç —Å—ç–∫–æ–Ω–æ–º–∏—Ç—å –≤—Ä–µ–º—è.
 */
interface IDataProvider<OutputData, InputData = null> {
  getData(): Readonly<OutputData>;
}

/**
 * –ê–±—Å—Ç—Ä–∞–∫—Ç–Ω—ã–π –∫–ª–∞—Å—Å –æ–ø–∏—Å–∞–Ω–∏–µ –æ–ø—Ä–µ–¥–µ–ª—è—é—â–∏–π
 * –ø–æ–ª–µ data –¥–æ—Å—Ç—É–ø–Ω—ã–π —Ç–æ–ª—å–∫–æ –ø–æ—Ç–æ–º–∫–∞ –∫–∞–∫
 * —Ç–æ–ª—å–∫–æ –¥–ª—è —á—Ç–µ–Ω–∏—è. –≠—Ç–æ –ø–æ–∑–≤–æ–ª–∏—Ç –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—Ç–∏—Ç—å
 * —Å–ª—É—á–∞–π–Ω–æ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –≤ –∫–ª–∞—Å—Å–∞—Ö –ø–æ—Ç–æ–º–∫–∞—Ö.
 */
abstract class DataProvider<InputData, OutputData = null> implements IDataProvider<InputData, OutputData> {
  constructor(protected data?: Readonly<OutputData>) {
  }

  abstract getData(): Readonly<InputData>
}



interface IPerson {
  firstName: string;
  lastName: string;
}

interface IPersonDataProvider {
  name: string;
}

class PersonDataProvider extends DataProvider<IPerson, IPersonDataProvider> {
    getData() {
      /**
       * –†–∞–±–æ—Ç–∞—è –≤ —Ç–µ–ª–µ –ø–æ—Ç–æ–º–∫–æ–≤ DataProvider
       * –±—É–¥–µ—Ç –Ω–µ —Ç–∞–∫ –ø—Ä–æ—Å—Ç–æ —Å–ª—É—á–∞–π–Ω–æ –∏–∑–º–µ–Ω–∏—Ç—å
       * –¥–∞–Ω–Ω—ã–µ –¥–æ—Å—Ç—É–ø–Ω—ã–µ —á–µ—Ä–µ–∑ —Å—Å—ã–ª–∫—É this.data
       */
      let [firstName, lastName] = this.data.name.split(` `);
      let result = { firstName, lastName };

      return result;
    }

}


let provider = new PersonDataProvider({ name: `Ivan Ivanov` });
`````

## Partial<T> (—Å–¥–µ–ª–∞—Ç—å –≤—Å–µ —á–ª–µ–Ω—ã –æ–±—ä–µ–∫—Ç–∞ –Ω–µ–æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–º–∏)

–°–æ–ø–æ—Å—Ç–∞–≤–∏–º—ã–π —Ç–∏–ø `Partial<T>` –¥–æ–±–∞–≤–ª—è–µ—Ç —á–ª–µ–Ω–∞–º –æ–±—ä–µ–∫—Ç–∞ –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä `?:` –¥–µ–ª–∞—è –∏—Ö —Ç–∞–∫–∏–º –æ–±—Ä–∞–∑–æ–º –Ω–µ–æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–º–∏.

`````ts
// lib.es6.d.ts

type Partial<T> = {
    [P in keyof T]?: T[P];
};
`````

–¢–∏–ø —Å–æ–ø–æ—Å—Ç–∞–≤–ª–µ–Ω–∏—è `Partial<T>` —è–≤–ª—è–µ—Ç—Å—è –≥–æ–º–æ–º–æ—Ä—Ñ–Ω—ã–º –∏ –Ω–µ –≤–ª–∏—è–µ—Ç –Ω–∞ —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä—ã, –∞ –ª–∏—à—å —Ä–∞—Å—à–∏—Ä—è–µ—Ç –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä—ã –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–≥–æ —Ç–∏–ø–∞.

`````ts
interface IPerson {
  readonly name: string; // –ø–æ–ª–µ –ø–æ–º–µ—á–µ–Ω–æ, –∫–∞–∫ —Ç–æ–ª—å–∫–æ –¥–ª—è —á—Ç–µ–Ω–∏—è
}

/**
 * –¥–æ–±–∞–≤–ª–µ–Ω –Ω–µ–æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä
 * –∏ –ø—Ä–∏ —ç—Ç–æ–º —Å–æ—Ö—Ä–∞–Ω–µ–Ω –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä readonly
 * 
 * type Person = {
 *  readonly name?: string;
 * }
 */
type Person = Partial<IPerson>
`````

–ü—Ä–µ–¥—Å—Ç–∞–≤—å—Ç–µ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–µ –∑–∞–≤–∏—Å—è—â–µ–µ –æ—Ç –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏, –∫–æ—Ç–æ—Ä–∞—è –∫–∞–∫ –ø–æ–ª–Ω–æ—Å—Ç—å—é, —Ç–∞–∫ –∏ —á–∞—Å—Ç–∏—á–Ω–æ, –º–æ–∂–µ—Ç –±—ã—Ç—å –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–º. –ü–æ—Å–∫–æ–ª—å–∫—É —Ä–∞–±–æ—Ç–æ—Å–ø–æ—Å–æ–±–Ω–æ—Å—Ç—å –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è –∑–∞–≤—è–∑–∞–Ω–∞ –Ω–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏, —á–ª–µ–Ω—ã –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã–µ –≤ —Ç–∏–ø–µ –ø—Ä–µ–¥—Å—Ç–∞–≤–ª—è—é—â–µ–º –µ—ë, –¥–æ–ª–∂–Ω—ã –±—ã—Ç—å –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–º–∏. –ù–æ –ø–æ—Å–∫–æ–ª—å–∫—É –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –º–æ–∂–µ—Ç –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å –ª–∏—à—å —á–∞—Å—Ç—å –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏, —Ñ—É–Ω–∫—Ü–∏—è –≤—ã–ø–æ–ª–Ω—è—é—â–∞—è –µ—ë —Å–ª–∏—è–Ω–∏–µ —Å –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–µ–π –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é, –Ω–µ –º–æ–∂–µ—Ç —É–∫–∞–∑–∞—Ç—å –≤ –∞–Ω–Ω–æ—Ç–∞—Ü–∏–∏ —Ç–∏–ø–∞ —É–∂–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã–π —Ç–∏–ø, —Ç–∞–∫ –∫–∞–∫ –µ–≥–æ —á–ª–µ–Ω—ã –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã. –û–ø–∏—Å—ã–≤–∞—Ç—å –Ω–æ–≤—ã–π —Ç–∏–ø, —Å–ª–∏—à–∫–æ–º —É—Ç–æ–º–∏—Ç–µ–ª—å–Ω–æ. –í —Ç–∞–∫–∏—Ö —Å–ª—É—á–∞—è—Ö –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –ø—Ä–∏–±–µ–≥–∞—Ç—å –∫ –ø–æ–º–æ—â–∏ `Partial<T>`.

`````ts
interface IConfig {
  domain: string;
  port: "80" | "90";
}

const DEFAULT_CONFIG: IConfig = {
  domain: `https://domain.com`,
  port: "80"
};


function createConfig(config: IConfig): IConfig {
  return Object.assign({}, DEFAULT_CONFIG, config);
}

/**
 * Error -> –ü–æ—Å–∫–æ–ª—å–∫—É –≤ —Ç–∏–ø–µ IConfig –≤—Å–µ
 * –ø–æ–ª—è –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ, –¥–∞–Ω–Ω—É—é —Ñ—É–Ω–∫—Ü–∏—é
 * –Ω–µ –ø–æ–ª—É—á–∏—Ç—Å—è –≤—ã–∑–≤–∞—Ç—å —Å —á–∞—Å—Ç–∏—á–Ω–æ–π –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–µ–π.
 */
createConfig({
  port: "80"
});


function createConfig(config:Partial<IConfig>): IConfig {
  return Object.assign({}, DEFAULT_CONFIG, config);
}

/**
 * Ok -> –¢–∏–ø Partial<T> —Å–¥–µ–ª–∞–ª –≤—Å–µ —á–ª–µ–Ω—ã
 * –æ–ø–∏—Å–∞–Ω–Ω—ã–µ –≤ IConfig –Ω–µ–æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–º–∏,
 * –ø–æ—ç—Ç–æ–º—É –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –º–æ–∂–µ—Ç –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–∏—Ç
 * –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—é —á–∞—Å—Ç–∏—á–Ω–æ.
 */
createConfig({
  port: "80"
});
`````

