let v2 = animal.getAnimalByID('5'); // Ok -> v2: IFish
let v3 = animal.getAnimalByID(true); // Ok -> v3: IInsect
let v4 = animal.getAnimalByID({}); // Ok -> v4: IReptile
`````

–ò—Å–∫–ª—é—á–µ–Ω–∏–µ–º –∏–∑ —ç—Ç–æ–≥–æ –ø—Ä–∞–≤–∏–ª–∞ —è–≤–ª—è—é—Ç—Å—è —Å–∏–≥–Ω–∞—Ç—É—Ä—ã, –∫–æ—Ç–æ—Ä—ã–µ –∏–º–µ—é—Ç –≤ —Å–≤–æ–µ–º –æ–ø–∏—Å–∞–Ω–∏–∏ –ª–∏—Ç–µ—Ä–∞–ª—å–Ω—ã–µ —Å—Ç—Ä–æ–∫–æ–≤—ã–µ —Ç–∏–ø—ã –¥–∞–Ω–Ω—ã—Ö (`literal String Types`). –î–µ–ª–æ –≤ —Ç–æ–º, —á—Ç–æ —Å–∏–≥–Ω–∞—Ç—É—Ä—ã —Å–æ–¥–µ—Ä–∂–∞—â–∏–µ –≤ —Å–≤–æ–µ–º –æ–ø–∏—Å–∞–Ω–∏–∏ –ª–∏—Ç–µ—Ä–∞–ª—å–Ω—ã–µ —Å—Ç—Ä–æ–∫–æ–≤—ã–µ —Ç–∏–ø—ã, –≤—Å–µ–≥–¥–∞ —Ä–∞–∑–º–µ—â–∞—é—Ç—Å—è –ø–µ—Ä–µ–¥ —Å–∏–≥–Ω–∞—Ç—É—Ä–∞–º–∏, —É –∫–æ—Ç–æ—Ä—ã—Ö –Ω–µ—Ç –≤ –æ–ø–∏—Å–∞–Ω–∏–∏ –ª–∏—Ç–µ—Ä–∞–ª—å–Ω—ã—Ö —Å—Ç—Ä–æ–∫–æ–≤—ã—Ö —Ç–∏–ø–æ–≤.

`````ts
interface IBird {}
interface IFish {}
interface IInsect {}
interface IReptile {}

// –¥–æ –∫–æ–º–ø–∏–ª—è—Ü–∏–∏
interface IAnimalFactory { 
    getAnimalByID(id: string): IBird;
}

interface IAnimalFactory {
    getAnimalByID(id: 'fish'): IFish;
}

interface IAnimalFactory {
    getAnimalByID(id: 'insect'): IInsect;
    getAnimalByID(id: number): IReptile;
}

/** –ø—Ä–∏ –∫–æ–º–ø–∏–ª—è—Ü–∏–∏
interface IAnimalFactory {
    getAnimalByID(id: 'fish'): IFish;
    getAnimalByID(id: 'insect'): IInsect;
    getAnimalByID(id: number): IReptile;
    getAnimalByID(id: string): IBird;
}
*/
`````
