function handler(event?: UserEvent): void {
    // —É–∫–∞–∑—ã–≤–∞–µ–º –∫–æ–º–ø–∏–ª—è—Ç–æ—Ä—É, —á—Ç–æ –±–µ—Ä–µ–º —ç—Ç–æ—Ç —É—á–∞—Å—Ç–æ–∫ –∫–æ–¥–∞ –ø–æ–¥ —Å–æ–±—Å—Ç–≤–µ–Ω–Ω—ã–π –∫–æ–Ω—Ç—Ä–æ–ª—å
    let type = event!.type; // Ok
