initialize();

console.log(value + value); // Error, –æ–±—Ä–∞—â–µ–Ω–∏–µ –∫ –ø–µ—Ä–µ–º–µ–Ω–Ω–æ–π –ø–µ—Ä–µ–¥ –ø—Ä–∏—Å–≤–æ–µ–Ω–∏–µ–º –µ–π –∑–Ω–∞—á–µ–Ω–∏—è

function initialize() {
  value = 0;
}
`````

–ß—Ç–æ–±—ã –∏–∑–±–µ–∂–∞—Ç—å –æ—à–∏–±–∫–∏ –ø—Ä–∏ –æ–±—Ä–∞—â–µ–Ω–∏–∏ –∫ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–º, –∫–æ—Ç–æ—Ä—ã–µ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω—ã –≤ —á—É–∂–æ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç–µ, –Ω—É–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å _definite assignment assertions_. _Definite assignment assertions_ —Ç–∞–∫–∂–µ —É–∫–∞–∑—ã–≤–∞–µ—Ç—Å—è —Å –ø–æ–º–æ—â—å—é —Å–∏–º–≤–æ–ª–∞ –≤–æ—Å–∫–ª–∏—Ü–∞—Ç–µ–ª—å–Ω–æ–≥–æ –∑–Ω–∞–∫–∞ (`!`) –∏ —Ä–∞—Å–ø–æ–ª–∞–≥–∞–µ—Ç—Å—è –ø–æ—Å–ª–µ –∏–¥–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ç–æ—Ä–∞ –ø–µ—Ä–µ–º–µ–Ω–Ω–æ–π. –£–∫–∞–∑—ã–≤–∞—è –¥–∞–Ω–Ω—ã–π –æ–ø–µ—Ä–∞—Ç–æ—Ä –∫–∞–∂–¥—ã–π —Ä–∞–∑ –ø—Ä–∏ –æ–±—Ä–∞—â–µ–Ω–∏–∏ –∫ –ø–µ—Ä–µ–º–µ–Ω–Ω–æ–π, —Ä–∞–∑—Ä–∞–±–æ—Ç—á–∏–∫ —Å–æ–æ–±—â–∞–µ—Ç –∫–æ–º–ø–∏–ª—è—Ç–æ—Ä—É, —á—Ç–æ –±–µ—Ä–µ—Ç –Ω–∞ —Å–µ–±—è –≤—Å–µ –ø—Ä–æ–±–ª–µ–º—ã, –∫–æ—Ç–æ—Ä—ã–µ –º–æ–≥—É—Ç –±—ã—Ç—å –≤—ã–∑–≤–∞–Ω—ã –æ—Ç—Å—É—Ç—Å—Ç–≤–∏–µ–º –∑–Ω–∞—á–µ–Ω–∏—è —É –ø–µ—Ä–µ–º–µ–Ω–Ω–æ–π.

`````ts
