let b = [200, 404] as const; // let b: readonly [200, 404]
let c = <const>[200, 404]; // let c: readonly [200, 404]
```

–í —Å–ª—É—á–∞–µ —Å –æ–±—ä–µ–∫—Ç–Ω—ã–º —Ç–∏–ø–æ–º, —É—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ –∫ –∫–æ–Ω—Å—Ç–∞–Ω—Ç–µ —Ä–µ–∫—É—Ä—Å–∏–≤–Ω–æ –ø–æ–º–µ—á–∞–µ—Ç –≤—Å–µ –µ–≥–æ –ø–æ–ª—è –∫–∞–∫ `readonly`. –ö—Ä–æ–º–µ —Ç–æ–≥–æ, –≤—Å–µ –µ–≥–æ –ø–æ–ª—è –ø—Ä–∏–Ω–∞–¥–ª–µ–∂–∞—â–∏–µ –∫ –ø—Ä–∏–º–∏—Ç–∏–≤–Ω—ã–º —Ç–∏–ø–∞–º —Ä–∞—Å—Ü–µ–Ω–∏–≤–∞—é—Ç—Å—è –∫–∞–∫ –ª–∏—Ç–µ—Ä–∞–ª—å–Ω—ã–µ —Ç–∏–ø—ã.

`````ts
type NotConstResponseType = {
    status: number;
    data: {
