  constructor(readonly name: string) {};

  public toString(): string {
      return StringUtil.toString(this.constructor.name);
  }
}
`````

`````ts
// –§–∞–π–ª animals/Fish.ts


import { IAnimal } from "../IAnimal";
import * as StringUtil from "../utils/string-util"

export default class Fish implements IAnimal {
  constructor(readonly name: string) {};

  public toString(): string {
      return StringUtil.toString(this.constructor.name);
  }
}
`````

`````ts
// –§–∞–π–ª Zoo.ts


import { IAnimal } from "./IAnimal";

export default class Zoo {
 private animalAll: IAnimal[ ] = [ ];

 public get length(): number {
     return this.animalAll.length;
 }

 public add(animal: IAnimal): void {
     this.animalAll.push(animal);
 }
 public getAnimalByIndex(index: number): IAnimal {
     return this.animalAll[index];
 }
}
`````

`````ts
// –§–∞–π–ª index.js


import Bird from "./animals/Bird";
import Fish from "./animals/Fish";

import Zoo from './Zoo';


const zoo: Zoo = new Zoo();

zoo.add( new Bird('raven') );
zoo.add( new Fish('shark') );

for( let i = 0; i < zoo.length; i++ ){
 console.log( `Animal name: ${ zoo.getAnimalByIndex(i).name }.` );
}
`````

`````ts
// –§–∞–π–ª index.lib.ts


/** imports */

import { IAnimal } from "./IAnimal";
import ZooCollection from './Zoo';

/** re-exports */

export {IAnimal} from './IAnimal'; // type

export {default as Bird} from './animals/Bird'; // type
export {default as Fish} from './animals/Fish'; // type

export {default as Zoo} from './Zoo'; // type

export const zoo: Zoo = new Zoo(); // instance
`````

–í –∫–æ–¥–µ –Ω–µ—Ç –Ω–∏—á–µ–≥–æ –Ω–µ–æ–±—ã—á–Ω–æ–≥–æ, –ø–æ—ç—Ç–æ–º—É –∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–µ–≤ –Ω–µ –±—É–¥–µ—Ç. –ï—Å–ª–∏ –∫–æ–º—É-—Ç–æ —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ —Ñ–∞–π–ª–∞ `index.lib.ts` –ø–æ–∫–∞–∑–∞–ª–æ—Å—å –Ω–µ–æ–±—ã—á–Ω—ã–º, —Ç–æ —Å—Ç–æ–∏—Ç –æ—Ç–º–µ—Ç–∏—Ç—å, —á—Ç–æ —ç—Ç–æ –æ–±—ã—á–Ω—ã–π —Ä–µ-—ç–∫—Å–ø–æ—Ä—Ç –º–æ–¥—É–ª–µ–π _JavaScript_, –∫–æ—Ç–æ—Ä—ã–π –Ω–∏–∫–∞–∫–æ–≥–æ –æ—Ç–Ω–æ—à–µ–Ω–∏—è –∫ _TypeScript_ –Ω–µ –∏–º–µ–µ—Ç. –ü–æ–≤—Ç–æ—Ä—é, —Ñ–∞–π–ª `index.lib.ts` —è–≤–ª—è–µ—Ç—Å—è —Ç–æ—á–∫–æ–π –≤—Ö–æ–¥–∞ —Å–æ–∑–¥–∞–≤–∞–µ–º–æ–π –±–∏–±–ª–∏–æ—Ç–µ–∫–∏, –ø–æ—ç—Ç–æ–º—É –æ–Ω –¥–æ–ª–∂–µ–Ω —ç–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –≤—Å–µ —Ç–æ, —á—Ç–æ –º–æ–∂–µ—Ç –ø–æ—Ç—Ä–µ–±–æ–≤–∞—Ç—å—Å—è –ø—Ä–∏ —Ä–∞–±–æ—Ç–µ —Å –Ω–µ–π. –ö–æ–Ω–∫—Ä–µ—Ç–Ω–æ –≤ —ç—Ç–æ–º —Å–ª—É—á–∞–µ —ç–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å _utils_ –Ω–∞—Ä—É–∂—É –Ω–µ –ø—Ä–µ–¥–ø–æ–ª–∞–≥–∞–µ—Ç—Å—è, –ø–æ—ç—Ç–æ–º—É –æ–Ω–∏ –Ω–µ –±—ã–ª–∏ —Ä–µ—ç–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω—ã.

–¢–∞–∫–∂–µ —Å—Ç–æ–∏—Ç –æ–±—Ä–∞—Ç–∏—Ç—å –≤–Ω–∏–º–∞–Ω–∏–µ –Ω–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–æ–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã _TypeScript_, –∫–æ—Ç–æ—Ä—ã–µ –≤–∑–∞–∏–º–Ω–æ –¥–æ–±–∞–≤–ª—è—é—Ç —Ç–æ—á–∫–∏ –≤—Ö–æ–¥–∞ –¥—Ä—É–≥ –¥—Ä—É–≥–∞ –≤ –∏—Å–∫–ª—é—á–µ–Ω–∏–µ. –ö—Ä–æ–º–µ —Ç–æ–≥–æ, –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–æ–Ω–Ω—ã–π —Ñ–∞–π–ª _dev-—Å–±–æ—Ä–∫–∏_ –∏—Å–∫–ª—é—á–∞–µ—Ç —Ç–∞–∫–∂–µ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–æ–Ω–Ω—ã–π —Ñ–∞–π–ª _prod-—Å–±–æ—Ä–∫–∏_.

